import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();

    this.state = {
      todos: [{
        id: 1, name: 'Buy some cloths'
      },{
        id: 2, name: 'Write Some Code'
      },{
        id: 3, name: 'Watch Avenger Endgame.'
      },{
        id: 4, name: 'Watch GOT S08'
      }]
    }
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1>React CRUD</h1>
        </header>

        <div className="container">
        <h2 className="text-center p-4">TODO APP</h2>

        <input type="text" className="my-4 form-control" placeholder="Add New TODO"/>
        <ul className="list-group">
          {this.state.todos.map((item, index) => {
            return <li key={item.id} className="list-group-item">{item.name}</li>
          })}

        </ul>
        </div>
      </div>
    );
  }
}

export default App;
