import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();

    this.state = {
      newTodo: '',
      editing: false,
      editingIndex: null,
      err: null,
      todos: [{
        id: 1, name: 'Buy some cloths'
      },{
        id: 2, name: 'Write Some Code'
      },{
        id: 3, name: 'Watch Avenger Endgame.'
      },{
        id: 4, name: 'Watch GOT S08'
      }]
    }
    this.addTodo = this.addTodo.bind(this);
    this.deleteTodo = this.deleteTodo.bind(this);
    this.editTodo = this.editTodo.bind(this);
    this.updateTodo = this.updateTodo.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.generateTodoId = this.generateTodoId.bind(this);
  }

  generateTodoId() {
    const lastTodo = this.state.todos[this.state.todos.length - 1];
    if(lastTodo) {
      return this.state.todos[this.state.todos.length - 1].id +1;
    }
    return 1;
  }

  addTodo() {
    if(this.state.newTodo != ''){
      const newTodo = {
        name: this.state.newTodo,
        id: this.generateTodoId()
      };
      //state is IMMUTABLE
      const todos = this.state.todos;
      todos.push(newTodo);
      this.setState({
        todos: todos,
        newTodo: ''
      });
    }else{
      this.setState({
        err: 'todo name cannot be empty' ,
      });
    }
  }
  handleChange(event) {
    //console.log(event.target.name, event.target.value);
    this.setState({
      newTodo: event.target.value
    });
  }
  deleteTodo(index) {
    //console.log(index);
    const todos = this.state.todos;
    delete todos[index];
    this.setState({
      todos: todos
    });
  }
  editTodo(index) {

    const todo = this.state.todos[index];
    this.setState({
      editing: true,
      newTodo: todo.name,
      editingIndex: index
    });
  }

  updateTodo() {

    const todo = this.state.todos[this.state.editingIndex];
    todo.name = this.state.newTodo;

    const todos = this.state.todos;
    todos[this.state.editingIndex] = todo;
    //console.log(this.state.newTodo);
    this.setState({
      todos: todos,
      newTodo: '',
      editing: false,
      editingIndex: null
    });
  }

  render() {
    const Clock = function(props) {
      const date = new Date();
      const thisYear = date.getFullYear();
      return <p style={{ color: props.color, fontSize: props.size }}>Copyright {thisYear}</p>
    }
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1>Todo App</h1>
        </header>

        <div className="container col-md-6">
        <h2 className="text-center p-4">Create new todo list</h2>

        <div class="input-group">
        <input 
          name="todo"
          type="text" 
          className="my-4 form-control" 
          placeholder="Add New TODO"
          onChange={this.handleChange}
          value={this.state.newTodo}
        />
        <span className="help-block">{this.state.err ? this.state.err : ''}</span>
        </div>
        <button 
          onClick={ this.state.editing ? this.updateTodo : this.addTodo}
          className="mb-4 btn btn-info form-control"
        >
          {this.state.editing ? 'Update Todo' : 'Add Todo'}
        </button>
        {

        !this.state.editing &&
        <ul className="list-group">
          {this.state.todos.map((item, index) => {
            return <li key={item.id} className="list-group-item">
                {item.name}

                <button 
                  type="button" 
                  className="right-btn btn btn-default" 
                  aria-label="Close"
                  onClick={() => {this.deleteTodo(index); }}
                >
                  <span aria-hidden="true"><i className="fas fa-trash"></i></span>
                </button>
                <button 
                  type="button" 
                  className="right-btn btn btn-default" 
                  aria-label="Close"
                  onClick={() => {this.editTodo(index); }}
                >
                  <span aria-hidden="true"><i className="fas fa-edit"></i></span>
                </button>
            </li>
          })}

        </ul>
        }
        </div>

        <footer className="App-footer">
          <h6>Design and Developed By <b><a href="" target="_blank">Himanshu Purohit</a></b></h6>
          <Clock color="#484848" size="18px"/>
        </footer>
      </div>
    );
  }
}

export default App;
