import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();

    this.state = {
      newTodo: '',
      todos: [{
        id: 1, name: 'Buy some cloths'
      },{
        id: 2, name: 'Write Some Code'
      },{
        id: 3, name: 'Watch Avenger Endgame.'
      },{
        id: 4, name: 'Watch GOT S08'
      }]
    }
    this.addTodo = this.addTodo.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  addTodo() {
    const newTodo = {
      name: this.state.newTodo,
      id: this.state.todos[this.state.todos.length - 1].id +1
    };
    //state is IMMUTABLE
    const todos = this.state.todos;
    todos.push(newTodo);
    this.setState({
      todos: todos
    });
  }
  handleChange(event) {
    //console.log(event.target.name, event.target.value);
    this.setState({
      newTodo: event.target.value
    });
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1>React CRUD</h1>
        </header>

        <div className="container">
        <h2 className="text-center p-4">TODO APP</h2>

        <input 
          name="todo"
          type="text" 
          className="my-4 form-control" 
          placeholder="Add New TODO"
          onChange={this.handleChange}
          value={this.state.newTodo}
        />
        <button 
          onClick={this.addTodo}
          className="mb-4 btn btn-info form-control"
        >Add TODO</button>
        <ul className="list-group">
          {this.state.todos.map((item, index) => {
            return <li key={item.id} className="list-group-item">{item.name}</li>
          })}

        </ul>
        </div>
      </div>
    );
  }
}

export default App;
